var form=document.getElementById("login_form"),username_input=document.getElementById("username"),password_input=document.getElementById("password"),statusbar=document.getElementsByClassName("statusbar")[0];Event.add(window,"load",()=>{Event.add(form,"submit",e=>{e.preventDefault();let t=username_input.value.trim(),s=password_input.value.trim();ajax("POST","/src/login.php",{username:t,password:s},e=>{"success"==e.responseText?window.location="../":(console.log(statusbar,e.responseText),statusbar.innerHTML=e.responseText)},e=>{alert("Error"),alert(e.responseText)})})});