var form=document.getElementById("login_form"),username_input=document.getElementById("username"),password_input=document.getElementById("password");Event.add(form,"submit",e=>{e.preventDefault();let t=username_input.value.trim(),r=password_input.value.trim();ajax("POST","/src/login.php",{username:t,password:r},e=>{console.log(e.responseText),"success"==e.responseText?window.location="../":alert(e.responseText)},e=>{alert("Error"),alert(e.responseText)})});