var form=document.getElementById("login_form"),username_input=document.getElementById("username"),password_input=document.getElementById("password"),statusbar=document.getElementsByClassName("statusbar")[0],loading=document.getElementsByClassName("loading")[0];Event.add(window,"load",()=>{Event.add(form,"submit",e=>{e.preventDefault();let s=username_input.value.trim(),t=password_input.value.trim();loading.classList.add("shown"),ajax("POST","/src/login.php",{username:s,password:t},e=>{"success"==e.responseText?window.location="../":(loading.classList.remove("shown"),statusbar.innerHTML=e.responseText,console.log(statusbar,e.responseText))},e=>{loading.classList.remove("shown"),alert("Error"),alert(e.responseText)})})});